import type { KaiosHTMLAudioElement } from "../../types/dom";

let _canAutoPlay = null;

// Note: Gecko mutes the sound outside the range 0.25 to 5.0
// Most browsers stop playing audio outside playbackRate bounds of 0.5 and 4.
export const validPlaybackRate = (rate: number): boolean => (
  !Number.isNaN(rate)
  && Number.isFinite(rate)
  && (rate >= 0.5 && rate <= 4.0)
);

function mozAutoPlay(): boolean {
  try {
    let audio = document.createElement('audio');
    audio.muted = true;
    if ('mozAutoplayEnabled' in audio) {
      const kaiosAudio = (audio as KaiosHTMLAudioElement);
      const autoplay = kaiosAudio.mozAutoplayEnabled;
      audio = null;
      return autoplay;
    }
  } catch (_) {
    return null;
  }
}

// Based on https://raw.githubusercontent.com/Modernizr/Modernizr/master/feature-detects/audio/autoplay.js
// License: MIT
function testAutoPlay(): Promise<boolean> {
  const waitTime = 200;
  const retries = 5;

  return new Promise((resolve) => {
    let timeout;
    let currentTry = 0;
    let elem = document.createElement('audio');
    elem.muted = true;
    let elemStyle = elem.style;

    function _testAutoplay(arg) {
      currentTry++;
      clearTimeout(timeout);

      const result = (arg && arg.type === 'playing' || elem.currentTime !== 0);

      if (!result && currentTry < retries) {
        // Detection can be flaky if the browser is slow, so lets retry in a little bit
        timeout = setTimeout(_testAutoplay, waitTime);
        return;
      }

      elem.removeEventListener('playing', _testAutoplay, false);

      // Cleanup, but don't assume elem is still in the page -
      // an extension may already have removed it.
      if (elem.parentNode) {
        elem.parentNode.removeChild(elem);
      }

      return resolve(result);
    }

    elemStyle.position = 'absolute';
    elemStyle.height = '0';
    elemStyle.width = '0';

    try {
      if (elem.canPlayType('audio/ogg') !== '') {
        elem.src = 'data:audio/ogg;base64,';
      } else if (elem.canPlayType('audio/wav') !== '') {
        elem.src = 'data:audio/wav;base64,UklGRjQAAABXQVZFZm10IBAAAAABAAEAEAAAABAAAAABAAgAZGF0YRAAAAB/f39/f39/f39/f39/f39/';
      } else {
        elem.src = 'data:audio/mpeg;base64,/+MYxAAAAANIAUAAAASEEB/jwOFM/0MM/90b/+RhST//w4NFwOjf///PZu////9lns5GFDv//l9GlUIEEIAAAgIg8Ir/JGq3/+MYxDsLIj5QMYcoAP0dv9HIjUcH//yYSg+CIbkGP//8w0bLVjUP///3Z0x5QCAv/yLjwtGKTEFNRTMuOTeqqqqqqqqqqqqq/+MYxEkNmdJkUYc4AKqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqq';
      }
    } catch (_) {
      return resolve(false);
    }

    elem.hidden = true;
    elem.setAttribute('autoplay', '');
    elemStyle.cssText = 'display:none';
    document.body.appendChild(elem);

    // Wait for the next tick to add the listener, otherwise the element may
    // not have time to play in high load situations (e.g. the test suite)
    setTimeout(() => {
      elem.addEventListener('playing', _testAutoplay, false);
      timeout = setTimeout(_testAutoplay, waitTime);
    }, 0);
  });
}

// @return [Boolean] True if the device supports autoplaying media
export function canAutoPlay(): Promise<boolean> {
  if (typeof _canAutoPlay === 'boolean') {
    return Promise.resolve(_canAutoPlay);
  }
  
  const mozCanAutoPlay = mozAutoPlay();
  if (typeof mozCanAutoPlay === 'boolean') {
    _canAutoPlay = mozCanAutoPlay;
    return Promise.resolve(_canAutoPlay);
  }

  return new Promise((resolve) => {
    testAutoPlay()
      .then((autoplay) => {
        _canAutoPlay = autoplay;
        resolve(_canAutoPlay);
      })
      .catch(() => {
        _canAutoPlay = false;
        resolve(_canAutoPlay);
      });
  });
}
